:"jsjj";s:0:"";s:4:"jslx";s:0:"";s:4:"jsys";s:0:"";s:3:"dna";s:0:"";s:4:"aljx";s:0:"";s:4:"note";s:0:"";s:5:"psize";s:2:"30";s:5:"inpic";s:0:"";s:7:"linkurl";s:0:"";s:6:"target";s:1:"0";s:9:"ordertype";s:14:"post_date:desc";s:7:"subcate";s:0:"";s:3:"ico";s:0:"";s:9:"small_pic";s:0:"";s:10:"medium_pic";s:0:"";s:7:"big_pic";s:0:"";s:6:"fields";s:14:"fwb,df,content";s:4:"p_id";N;s:10:"p_parentid";N;s:4:"s_id";s:3:"179";s:10:"s_parentid";s:3:"178";}[§[№[※[◆]※]№]§]fd8ada13ff8cd7a2b03562b1c2e67f3e[◆[№[-[※]-]№]◆]1455775417[◆[№[-[※]-]№]◆]a:3:{i:0;a:34:{s:2:"id";s:3:"181";s:10:"identifier";s:8:"wstphyjk";s:6:"langid";s:2:"zh";s:9:"cate_name";s:24:"微生态平衡与健康";s:8:"parentid";s:3:"178";s:9:"module_id";s:1:"7";s:9:"tpl_index";s:0:"";s:8:"tpl_list";s:18:"list_article_wst_1";s:8:"tpl_file";s:0:"";s:8:"if_index";s:1:"0";s:5:"taxis";s:3:"255";s:6:"status";s:1:"1";s:9:"if_hidden";s:1:"0";s:8:"seotitle";s:0:"";s:8:"keywords";s:0:"";s:11:"description";s:0:"";s:6:"ifspec";s:1:"0";s:4:"jsjj";s:969:"<p style="text-align: center;"><img src="upfiles/201509/25/big_638.jpg" border="0" alt="" /><span style="font-size: 13px;">&nbsp;</span></p><p><span style="font-size: 13px;"></span></p><span style="font-size:13px;">&nbsp; &nbsp; &nbsp; &nbsp;在人体体表和与外界相通的腔道中，寄居着不同种类和数量对人体无损害作用的微生物，通称为正常微生物群或正常菌群。正常菌群在宿主出生后，即在体内建立并持续存在，有规律地定居于特定部位，成为宿主不可缺少的组成部分。一个健康成年人大约由10<sup>13</sup>个体细胞组成，而全身定殖的正常微生物总数高达10<sup>14</sup>个，主要分布于皮肤、口腔、消化道、呼吸道和泌尿生殖道。正常微生物群具有较强的自身稳定性，能阻止外籍菌的定植。如果正常微生物群的稳态被打破，就会引起许多疾病。</span><p><span style="font-size:13px;"></span></p><p></p>";s:4:"jslx";s:0:"";s:4:"jsys";s:0:"";s:3:"dna";s:0:"";s:4:"aljx";s:0:"";s:4:"note";s:0:"";s:5:"psize";s:2:"30";s:5:"inpic";s:0:"";s:7:"linkurl";s:0:"";s:6:"target";s:1:"0";s:9:"ordertype";s:14:"post_date:desc";s:7:"subcate";s:0:"";s:3:"ico";s:30:"tpl/www/images/banner-yxfw.jpg";s:9:"small_pic";s:0:"";s:10:"medium_pic";s:0:"";s:7:"big_pic";s:0:"";s:6:"fields";s:14:"fwb,df,content";}i:1;a:34:{s:2:"id";s:3:"180";s:10:"identifier";s:7:"azzdyyy";s:6:"langid";s:2:"zh";s:9:"cate_name";s:21:"癌症诊断与用药";s:8:"parentid";s:3:"178";s:9:"module_id";s:1:"7";s:9:"tpl_index";s:0:"";s:8:"tpl_list";s:17:"list_article_az_1";s:8:"tpl_file";s:0:"";s:8:"if_index";s:1:"0";s:5:"taxis";s:3:"255";s:6:"status";s:1:"1";s:9:"if_hidden";s:1:"0";s:8:"seotitle";s:0:"";s:8:"keywords";s:0:"";s:11:"description";s:0:"";s:6:"ifspec";s:1:"0";s:4:"jsjj";s:870:"<div style="text-align: center;"><img src="upfiles/201509/25/big_636.jpg" border="0" alt="" /></div><div style="text-align: center;"><br /></div><div style="text-align: left;"><span style="font-size:13px;">&nbsp; &nbsp; &nbsp; &nbsp;癌症，被冠以人类健康的“头号杀手”，百姓中流传着得了癌症就等于“判了死刑”的说法。但是有些癌症若能早发现，就可以在最佳治疗时期获得极佳疗效，甚至可以治愈。对于发现于晚期的癌症，也不要丧失信心，许多肿瘤医院，可以针对患者的病情状况、不良反应、生活质量等因素，进行个体化精准治疗。个体化精准治疗的神奇就在于，之前的检测都是利用影像学和组织学进行，进入精准诊疗时代后，是通过高通量基因测序帮助确诊，并指导靶向药物用药。</span><br /></div>";s:4:"jslx";s:0:"";s:4:"jsys";s:0:"";s:3:"dna";s:0:"";s:4:"aljx";s:0:"";s:4:"note";s:0:"";s:5:"psize";s:2:"30";s:5:"inpic";s:0:"";s:7:"linkurl";s:0:"";s:6:"target";s:1:"0";s:9:"ordertype";s:14:"post_date:desc";s:7:"subcate";s:0:"";s:3:"ico";s:30:"tpl/www/images/banner-yxfw.jpg";s:9:"small_pic";s:0:"";s:10:"medium_pic";s:0:"";s:7:"big_pic";s:0:"";s:6:"fields";s:14:"fwb,df,content";}i:2;a:34:{s:2:"id";s:3:"179";s:10:"identifier";s:4:"ysyy";s:6:"langid";s:2:"zh";s:9:"cate_name";s:12:"优生优育";s:8:"parentid";s:3:"178";s:9:"module_id";s:1:"7";s:9:"tpl_index";s:0:"";s:8:"tpl_list";s:17:"list_article_ys_1";s:8:"tpl_file";s:0:"";s:8:"if_index";s:1:"0";s:5:"taxis";s:3:"255";s:6:"status";s:1:"1";s:9:"if_hidden";s:1:"0";s:8:"seotitle";s:0:"";s:8:"keywords";s:0:"";s:11:"description";s:0:"";s:6:"ifspec";s:1:"0";s:4:"jsjj";s:1479:"<div style="text-align: center;"><img src="upfiles/201509/25/big_637.jpg" border="0" alt="" /></div><div style="text-align: center;"><br /></div><div style="text-align: left;"><span style="color: rgb(255, 153, 102);"><span style="font-size:10px;">1.<span style="white-space: pre;">	</span>我国每年约有80万-120万出生缺陷儿，约占每年出生人口总数的4%-6%。<br />2.<span style="wh